package com.mjw.slidingwindow;

import java.util.HashMap;
import java.util.Map;

/**
 * @Description :
 * @Date : 2022/6/29
 * @Author : jiawei_mao
 **/
public class LeetCode395 {

    public static void main(String[] args) {
        String s = "";
        int k = 301;
        System.out.println(longestSubstring(s, k));

    }

    public static int longestSubstring(String s, int k) {
        int windowLength = s.length();
        Boolean flag = true;
        int left, right;
        for (int i = windowLength; i >= k; i--) {
            if (flag) {
                left = 0;
                right = windowLength;
                if(isOK(s.substring(left, right),k)){
                    return right-left;
                }
                while (right < s.length()) {
                    left++;
                    right++;
                    if(isOK(s.substring(left, right),k)){
                        return right-left;
                    }
                }
                flag = !flag;
            } else {
                left = s.length() - windowLength;
                right = s.length();
                if(isOK(s.substring(left, right),k)){
                    return right-left;
                }
                while (left > 0) {
                    left--;
                    right--;
                    if(isOK(s.substring(left, right),k)){
                        return right-left;
                    }
                }
                flag = !flag;
            }
            windowLength--;
        }
        return 0;
    }

    public static boolean isOK(String s, int k) {
        Map<Character, Integer> map = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            Integer orDefault = map.getOrDefault(s.charAt(i), 0);
            map.put(s.charAt(i), orDefault + 1);
        }
        boolean flag = true;
        for (Integer value : map.values()) {
            if (value < k) {
                flag = false;
            }
        }
        return flag;
    }

}
